public class UserMap : ClassMap<User>
{
  public UserMap()
  {
    Map(x => x.Name)
      .Named("displayname");
    Map(x => x.Age);
  }
}

public class UserMap : SubClassMap<User>
{
  public UserMap() : base(new SuperUserMap())
  {
    Map(x => x.Name)
      .Named("displayname");
    Map(x => x.Age);
  }
}
